  (10918.0, 'SR9 07 02 02', 'Sadzenie tawuły japońskiej ''Goldflame''', 'szt.', NULL, 'Złote liście, różowe kwiaty, niska, idealna na obwódki.', NULL, 10.0, 3397.0, 'Krzewy ozdobne', 'SR9 07 02', 'Ogrodnictwo / Rośliny - sadzenie / Krzewy ozdobne', NULL),
  (10930.0, 'SR9 07 04 05', 'Sadzenie barw', 'szt.', NULL, 'Okrywowa, wiecznie zielona, do cienia, mało wymagająca.', NULL, 5.0, 3399.0, 'Trawy i byliny', 'SR9 07 04', 'Ogrodnictwo / Rośliny - sadzenie / Trawy i byliny', NULL),
  (10927.0, 'SR9 07 04 02', 'Sadzenie kostrzewy sinej', 'szt.', NULL, 'Kulista, niebieska trawa, lubi słońce i suche gleby.', NULL, 6.0, 3399.0, 'Trawy i byliny', 'SR9 07 04', 'Ogrodnictwo / Rośliny - sadzenie / Trawy i byliny', NULL),
  (10929.0, 'SR9 07 04 04', 'Sadzenie lawendy wąskolistnej', 'szt.', NULL, 'Fioletowe kwiaty, aromatyczna, lubi słońce i wapienne gleby.', NULL, 7.0, 3399.0, 'Trawy i byliny', 'SR9 07 04', 'Ogrodnictwo / Rośliny - sadzenie / Trawy i byliny', NULL),
